import React from 'react';
import styled from 'styled-components';

const HomeContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Hero = styled.div`
  background-image: url('/home.png');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  padding: 8rem 2rem 4rem 2rem; /* More top padding for text visibility */
  text-align: center;
  margin-bottom: 2rem;
  border-radius: 16px;
  box-shadow: 0 4px 24px rgba(0,0,0,0.15);
  position: relative;
  color: #fff;
  /* Optional: add a dark overlay for better text contrast */
  &::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background: rgba(0,0,0,0.45);
    border-radius: 16px;
    z-index: 1;
  }
  > * {
    position: relative;
    z-index: 2;
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  color: #666;
`;

function Home() {
  return (
    <HomeContainer>
      <Hero>
        <Title>Gürdal Oto Kaporta</Title>
        <Subtitle>Profesyonel Hasar Onarım Merkezi</Subtitle>
      </Hero>
      <div>
        <h2>Hoş Geldiniz</h2>
        <p>
          Gürdal Oto Kaporta olarak, araçlarınızın hasar onarımında uzman ekibimizle
          hizmetinizdeyiz. Modern ekipmanlarımız ve deneyimli personelimizle
          araçlarınıza en iyi hizmeti sunuyoruz.
        </p>
      </div>
    </HomeContainer>
  );
}

export default Home;
