import React from 'react';
import styled from 'styled-components';
import { FaTools, FaPaintRoller, FaCarCrash, FaShieldAlt, FaCheckCircle, FaClock, FaStar, FaUserFriends } from "react-icons/fa";

const accentColor = "#e63946";
const darkColor = "#22223b";
const lightColor = "#f8f9fa";

const HomeContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  font-family: 'Poppins', 'Roboto', sans-serif;
  background: ${lightColor};
`;

const Hero = styled.div`
  background-image: url('/home.png');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  padding: 8rem 2rem 4rem 2rem;
  text-align: center;
  margin-bottom: 2.5rem;
  border-radius: 20px;
  box-shadow: 0 6px 32px rgba(0,0,0,0.18);
  position: relative;
  color: #fff;
  overflow: hidden;
  &::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background: rgba(34,34,59,0.55);
    border-radius: 20px;
    z-index: 1;
  }
  > * {
    position: relative;
    z-index: 2;
  }
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: ${accentColor};
  letter-spacing: 1px;
  text-shadow: 0 2px 8px rgba(0,0,0,0.18);
`;

const Subtitle = styled.p`
  font-size: 1.4rem;
  color: #fff;
  font-weight: 500;
  margin-bottom: 2rem;
  text-shadow: 0 1px 6px rgba(0,0,0,0.18);
`;

const CTAButton = styled.a`
  display: inline-block;
  background: ${accentColor};
  color: #fff;
  font-weight: 600;
  font-size: 1.1rem;
  padding: 0.9rem 2.2rem;
  border-radius: 30px;
  text-decoration: none;
  box-shadow: 0 2px 12px rgba(230,57,70,0.15);
  transition: background 0.2s, transform 0.2s;
  margin-top: 1rem;
  &:hover {
    background: #a4161a;
    transform: translateY(-2px) scale(1.04);
  }
`;

const Section = styled.section`
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 2px 12px rgba(34,34,59,0.06);
  padding: 2.5rem 2rem;
  margin-bottom: 2rem;
  text-align: center;
  @media (min-width: 900px) {
    text-align: left;
    padding: 3rem 4rem;
  }
`;

const WelcomeGrid = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  align-items: center;
  @media (min-width: 900px) {
    flex-direction: row;
    align-items: flex-start;
  }
`;

const WelcomeText = styled.div`
  flex: 2;
`;

const ServicesList = styled.ul`
  flex: 1;
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.2rem;
  @media (min-width: 600px) {
    grid-template-columns: 1fr 1fr;
  }
`;

const ServiceItem = styled.li`
  display: flex;
  align-items: center;
  gap: 0.7rem;
  font-size: 1.08rem;
  color: #333;
  background: #f1f3f6;
  border-radius: 10px;
  padding: 0.7rem 1rem;
  font-weight: 500;
  svg {
    color: ${accentColor};
    font-size: 1.3rem;
  }
`;

const SectionTitle = styled.h2`
  color: ${accentColor};
  font-size: 2rem;
  font-weight: 600;
  margin-bottom: 1.2rem;
  text-align: center;
`;

const SectionText = styled.p`
  color: #444;
  font-size: 1.15rem;
  line-height: 1.7;
  margin-bottom: 0.5rem;
`;

const AdvantagesGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;
  margin-top: 1.5rem;
  @media (max-width: 600px) {
    grid-template-columns: 1fr;
  }
`;

const AdvantageCard = styled.div`
  background: #f1f3f6;
  border-radius: 12px;
  padding: 1.2rem 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  font-size: 1.08rem;
  color: #333;
  font-weight: 500;
  box-shadow: 0 1px 6px rgba(34,34,59,0.04);
  svg {
    color: ${accentColor};
    font-size: 1.7rem;
    flex-shrink: 0;
  }
`;

function Home() {
  return (
    <HomeContainer>
      <Hero>
        <Title>Gürdal Oto Kaporta</Title>
        <Subtitle>
          Aracınızın değerini koruyun, profesyonel ekibimize güvenin.<br />
          Hasar onarımında 30+ yıl tecrübe!
        </Subtitle>
        <CTAButton href="/iletisim">Hemen Teklif Al</CTAButton>
      </Hero>
      <Section>
        <SectionTitle>Hoş Geldiniz</SectionTitle>
        <WelcomeGrid>
          <WelcomeText>
            <SectionText>
              Gürdal Oto Kaporta, modern ekipmanlar ve uzman kadrosuyla aracınızın tüm kaporta ve boya işlemlerinde yanınızda. 
              Müşteri memnuniyetini ön planda tutarak, hızlı ve kaliteli hizmet sunuyoruz.
            </SectionText>
            <SectionText>
              <b>Avantajlarımız:</b> Orijinal parça kullanımı, şeffaf fiyatlandırma, ücretsiz ekspertiz ve hızlı teslimat.
            </SectionText>
          </WelcomeText>
          <ServicesList>
            <ServiceItem><FaTools /> Kaporta Onarımı</ServiceItem>
            <ServiceItem><FaPaintRoller /> Boya & Mini Onarım</ServiceItem>
            <ServiceItem><FaCarCrash /> Cam Değişimi</ServiceItem>
            <ServiceItem><FaShieldAlt /> Sigorta İşlemleri</ServiceItem>
          </ServicesList>
        </WelcomeGrid>
      </Section>
      <Section>
        <SectionTitle>Neden Bizi Tercih Etmelisiniz?</SectionTitle>
        <AdvantagesGrid>
          <AdvantageCard><FaCheckCircle /> 30+ yıl sektör deneyimi</AdvantageCard>
          <AdvantageCard><FaStar /> Son teknoloji ekipmanlar</AdvantageCard>
          <AdvantageCard><FaShieldAlt /> Sigorta anlaşmalı hizmet</AdvantageCard>
          <AdvantageCard><FaUserFriends /> Müşteri odaklı yaklaşım</AdvantageCard>
          <AdvantageCard><FaClock /> Hızlı ve güvenilir teslimat</AdvantageCard>
        </AdvantagesGrid>
      </Section>
    </HomeContainer>
  );
}

export default Home;
