function CarsForSale() {
  // const { data: cars, loading, error, execute: loadCars } = useApi(api.getCars);
  const [cars, setCars] = React.useState([
    {
      _id: '1',
      image: '/car-sale-1.jpg',
      title: 'Renault Clio',
      year: 2018,
      km: 45000,
      details: 'Dizel, Otomatik',
      price: '350.000 TL',
      color: 'Beyaz',
      fuelType: 'Dizel',
      transmission: 'Otomatik',
      photos: ['/car-sale-1.jpg', '/car-sale-1.jpg']
    },
    {
      _id: '2',
      image: '/car-sale-2.jpg',
      title: 'Fiat Egea',
      year: 2020,
      km: 32000,
      details: 'Benzinli, Manuel',
      price: '420.000 TL',
      color: 'Gri',
      fuelType: 'Benzinli',
      transmission: 'Manuel',
      photos: ['/car-sale-2.jpg', '/car-sale-2.jpg']
    }
    // ...add more mock cars
  ]);
  const [loading] = React.useState(false);
  const [error] = React.useState(null);
  const [filters, setFilters] = React.useState({
    search: '',
    minPrice: '',
    maxPrice: ''
  });
  const [selectedCar, setSelectedCar] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);

  // useEffect(() => {
  //   loadCars();
  // }, [loadCars]);

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const filteredCars = cars?.filter(car => {
    const matchesSearch = car.title.toLowerCase().includes(filters.search.toLowerCase()) ||
                         car.details.toLowerCase().includes(filters.search.toLowerCase());
    const price = parseInt(car.price.replace(/[^0-9]/g, ''));
    const matchesMinPrice = !filters.minPrice || price >= parseInt(filters.minPrice);
    const matchesMaxPrice = !filters.maxPrice || price <= parseInt(filters.maxPrice);

    return matchesSearch && matchesMinPrice && matchesMaxPrice;
  });

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <CarsContainer>
      <PageTitle>Satılık Araçlar</PageTitle>

      <FilterContainer>
        <FilterInput
          type="text"
          name="search"
          placeholder="Araç ara..."
          value={filters.search}
          onChange={handleFilterChange}
        />
        <FilterInput
          type="number"
          name="minPrice"
          placeholder="Min. Fiyat"
          value={filters.minPrice}
          onChange={handleFilterChange}
        />
        <FilterInput
          type="number"
          name="maxPrice"
          placeholder="Max. Fiyat"
          value={filters.maxPrice}
          onChange={handleFilterChange}
        />
      </FilterContainer>

      <CarGrid>
        {filteredCars?.map((car) => (
          <CarCard key={car._id}>
            <CarImage src={car.image} alt={car.title} />
            <CarInfo>
              <CarTitle>{car.title}</CarTitle>
              <CarAttributes>
                <Attribute>Yıl: {car.year}</Attribute>
                <Attribute>Km: {car.km}</Attribute>
                {car.color && <Attribute>Renk: {car.color}</Attribute>}
                {car.fuelType && <Attribute>Yakıt: {car.fuelType}</Attribute>}
                {car.transmission && <Attribute>Vites: {car.transmission}</Attribute>}
              </CarAttributes>
              <CarDetails>{car.details}</CarDetails>
              <Price>{car.price}</Price>
              {car.photos && car.photos.length > 1 && (
                <PhotosContainer>
                  {car.photos.map((photo, idx) => (
                    <PhotoThumb key={idx} src={photo} alt={`Fotoğraf ${idx+1}`} />
                  ))}
                </PhotosContainer>
              )}
              <DetailsButton onClick={() => setSelectedCar(car)}>Detayları Gör</DetailsButton>
            </CarInfo>
          </CarCard>
        ))}
      </CarGrid>

      {selectedCar && (
        <Modal onClick={() => setSelectedCar(null)}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalTitle>{selectedCar.title}</ModalTitle>
            <ModalImages>
              {selectedCar.photos.map((photo, idx) => (
                <ModalImage 
                  key={idx} 
                  src={photo} 
                  alt={`Fotoğraf ${idx+1}`} 
                  onClick={() => setSelectedImage(photo)}
                />
              ))}
            </ModalImages>
            <ModalDetails>
              <ModalAttribute>
                <span>Yıl:</span> {selectedCar.year}
              </ModalAttribute>
              <ModalAttribute>
                <span>Km:</span> {selectedCar.km}
              </ModalAttribute>
              {selectedCar.color && (
                <ModalAttribute>
                  <span>Renk:</span> {selectedCar.color}
                </ModalAttribute>
              )}
              {selectedCar.fuelType && (
                <ModalAttribute>
                  <span>Yakıt:</span> {selectedCar.fuelType}
                </ModalAttribute>
              )}
              {selectedCar.transmission && (
                <ModalAttribute>
                  <span>Vites:</span> {selectedCar.transmission}
                </ModalAttribute>
              )}
              <ModalAttribute>
                <span>Fiyat:</span> {selectedCar.price}
              </ModalAttribute>
              <ModalAttribute>
                <span>Detaylar:</span> {selectedCar.details}
              </ModalAttribute>
            </ModalDetails>
            <CloseButton onClick={() => setSelectedCar(null)}>×</CloseButton>
          </ModalContent>
        </Modal>
      )}

      {selectedImage && (
        <FullSizeModal onClick={() => setSelectedImage(null)}>
          <FullSizeImage 
            src={selectedImage} 
            alt="Full size" 
            onClick={(e) => e.stopPropagation()} 
          />
          <CloseButton onClick={() => setSelectedImage(null)}>×</CloseButton>
        </FullSizeModal>
      )}
    </CarsContainer>
  );
}

export default CarsForSale;
