import React from 'react';
import styled from 'styled-components';

const HomeContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Hero = styled.div`
  background-color: #f5f5f5;
  padding: 4rem 2rem;
  text-align: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  color: #666;
`;

function Home() {
  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <img
        src="/home.png"
        alt="Home"
        style={{
          maxWidth: '90%',
          height: 'auto',
          borderRadius: '16px',
          boxShadow: '0 4px 24px rgba(0,0,0,0.15)',
          marginBottom: '2rem'
        }}
      />
      <HomeContainer>
        <Hero>
          <Title>Gürdal Oto Kaporta</Title>
          <Subtitle>Profesyonel Hasar Onarım Merkezi</Subtitle>
        </Hero>
        <div>
          <h2>Hoş Geldiniz</h2>
          <p>
            Gürdal Oto Kaporta olarak, araçlarınızın hasar onarımında uzman ekibimizle
            hizmetinizdeyiz. Modern ekipmanlarımız ve deneyimli personelimizle
            araçlarınıza en iyi hizmeti sunuyoruz.
          </p>
        </div>
      </HomeContainer>
    </div>
  );
}

export default Home;
