import React, { useEffect } from 'react';
import styled from 'styled-components';
// import { useApi } from '../hooks/useApi';
// import { api } from '../services/api';
import LoadingSpinner from '../components/common/LoadingSpinner';
import ErrorMessage from '../components/common/ErrorMessage';

const accentColor = "#e63946";

const CarsContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  font-family: 'Poppins', 'Roboto', sans-serif;
`;

const PageTitle = styled.h1`
  color: ${accentColor};
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  text-align: center;
  letter-spacing: 0.5px;
`;

const CarGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
`;

const CarCard = styled.div`
  background: linear-gradient(90deg, #232526 0%, #414345 100%);
  border-radius: 18px;
  box-shadow: 0 1px 6px rgba(34,34,59,0.18);
  overflow: hidden;
  transition: transform 0.3s, box-shadow 0.3s;

  &:hover {
    transform: translateY(-5px) scale(1.03);
    box-shadow: 0 4px 18px rgba(34,34,59,0.18);
  }
`;

const CarImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-top-left-radius: 18px;
  border-top-right-radius: 18px;
`;

const CarInfo = styled.div`
  padding: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const CarTitle = styled.h3`
  margin-bottom: 0.5rem;
  color: #fff;
  font-size: 1.3rem;
  font-weight: 700;
`;

const CarAttributes = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.7rem 1.5rem;
  margin-bottom: 0.5rem;
`;

const Attribute = styled.span`
  color: #eee;
  font-size: 0.98rem;
  background: rgba(255,255,255,0.07);
  border-radius: 8px;
  padding: 0.2rem 0.7rem;
`;

const CarDetails = styled.p`
  color: #eee;
  margin-bottom: 0.5rem;
  font-size: 1rem;
`;

const Price = styled.div`
  font-size: 1.2rem;
  font-weight: bold;
  color: ${accentColor};
  margin-top: 1rem;
`;

const FilterContainer = styled.div`
  margin-bottom: 2rem;
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
`;

const FilterInput = styled.input`
  padding: 0.8rem;
  border: 2px solid ${accentColor};
  border-radius: 14px;
  min-width: 200px;
  font-size: 1rem;
  font-family: inherit;
  outline: none;
  transition: border 0.2s, box-shadow 0.2s;
  &:focus {
    border: 2px solid #232526;
    box-shadow: 0 0 0 2px ${accentColor}33;
  }
  &::placeholder {
    color: #888;
    opacity: 1;
  }
`;

const PhotosContainer = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-top: 0.5rem;
  flex-wrap: wrap;
`;

const PhotoThumb = styled.img`
  width: 60px;
  height: 40px;
  object-fit: cover;
  border-radius: 6px;
  border: 1.5px solid #232526;
  background: #232526;
`;

const DetailsButton = styled.button`
  margin-top: auto;
  padding: 0.7rem 1.5rem;
  background: ${accentColor};
  color: #fff;
  border: none;
  border-radius: 10px;
  font-weight: 600;
  font-size: 1rem;
  cursor: pointer;
  transition: background 0.2s, transform 0.2s;
  &:hover {
    background: #a4161a;
    transform: scale(1.04);
  }
`;

function CarsForSale() {
  // const { data: cars, loading, error, execute: loadCars } = useApi(api.getCars);
  const [cars, setCars] = React.useState([
    {
      _id: '1',
      image: '/car-sale-1.jpg',
      title: 'Renault Clio',
      year: 2018,
      km: 45000,
      details: 'Dizel, Otomatik',
      price: '350.000 TL',
      color: 'Beyaz',
      fuelType: 'Dizel',
      transmission: 'Otomatik',
      photos: ['/car-sale-1.jpg', '/car-sale-1.jpg']
    },
    {
      _id: '2',
      image: '/car-sale-2.jpg',
      title: 'Fiat Egea',
      year: 2020,
      km: 32000,
      details: 'Benzinli, Manuel',
      price: '420.000 TL',
      color: 'Gri',
      fuelType: 'Benzinli',
      transmission: 'Manuel',
      photos: ['/car-sale-2.jpg', '/car-sale-2.jpg']
    }
    // ...add more mock cars
  ]);
  const [loading] = React.useState(false);
  const [error] = React.useState(null);
  const [filters, setFilters] = React.useState({
    search: '',
    minPrice: '',
    maxPrice: ''
  });

  // useEffect(() => {
  //   loadCars();
  // }, [loadCars]);

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const filteredCars = cars?.filter(car => {
    const matchesSearch = car.title.toLowerCase().includes(filters.search.toLowerCase()) ||
                         car.details.toLowerCase().includes(filters.search.toLowerCase());
    const price = parseInt(car.price.replace(/[^0-9]/g, ''));
    const matchesMinPrice = !filters.minPrice || price >= parseInt(filters.minPrice);
    const matchesMaxPrice = !filters.maxPrice || price <= parseInt(filters.maxPrice);

    return matchesSearch && matchesMinPrice && matchesMaxPrice;
  });

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <CarsContainer>
      <PageTitle>Satılık Araçlar</PageTitle>

      <FilterContainer>
        <FilterInput
          type="text"
          name="search"
          placeholder="Araç ara..."
          value={filters.search}
          onChange={handleFilterChange}
        />
        <FilterInput
          type="number"
          name="minPrice"
          placeholder="Min. Fiyat"
          value={filters.minPrice}
          onChange={handleFilterChange}
        />
        <FilterInput
          type="number"
          name="maxPrice"
          placeholder="Max. Fiyat"
          value={filters.maxPrice}
          onChange={handleFilterChange}
        />
      </FilterContainer>

      <CarGrid>
        {filteredCars?.map((car) => (
          <CarCard key={car._id}>
            <CarImage src={car.image} alt={car.title} />
            <CarInfo>
              <CarTitle>{car.title}</CarTitle>
              <CarAttributes>
                <Attribute>Yıl: {car.year}</Attribute>
                <Attribute>Km: {car.km}</Attribute>
                {car.color && <Attribute>Renk: {car.color}</Attribute>}
                {car.fuelType && <Attribute>Yakıt: {car.fuelType}</Attribute>}
                {car.transmission && <Attribute>Vites: {car.transmission}</Attribute>}
              </CarAttributes>
              <CarDetails>{car.details}</CarDetails>
              <Price>{car.price}</Price>
              {car.photos && car.photos.length > 1 && (
                <PhotosContainer>
                  {car.photos.map((photo, idx) => (
                    <PhotoThumb key={idx} src={photo} alt={`Fotoğraf ${idx+1}`} />
                  ))}
                </PhotosContainer>
              )}
              <DetailsButton>Detayları Gör</DetailsButton>
            </CarInfo>
          </CarCard>
        ))}
      </CarGrid>
    </CarsContainer>
  );
}

export default CarsForSale;
